mapper.select:
SELECT count(*)
FROM accompany
WHERE user_id = #{userId}

mapper.update:
UPDATE accompany
SET title = #{title}, content = #{content}, hashtag = #{hashtag}, chat_url = #{chatUrl}, member_cnt = #{memberCnt}, thumbnail = #{thumbnail}
WHERE id = #{id} AND user_id = #{userId}

UPDATE accompany
SET view_cnt = view_cnt + 1
WHERE id = #{id}

mapper.select:

WHERE status = 0
ORDER BY created_at DESC, id DESC
LIMIT #{offset}, #{pageSize}

SELECT count(*)
FROM accompany
WHERE user_id = #{userId} and date(created_at) = date(now());
