mapper.update:
UPDATE combined_board
SET   title = #{title}
  , content = #{content}
  , updated_at = now()
WHERE id = #{id}

UPDATE combined_board
SET   view_cnt = view_cnt + 1
WHERE id = #{id}

mapper.select:
SELECT id, menu_code, title, content, view_cnt, status, created_at, created_by, updated_at, updated_by FROM combined_board WHERE true
AND title LIKE concat('%', #{sc.keyword}, '%')
AND user_id LIKE concat('%', #{sc.keyword}, '%')
AND (title LIKE concat('%', #{sc.keyword}, '%') OR content LIKE concat('%', #{sc.keyword}, '%'))
and menu_code = #{menuCode}
ORDER BY created_at DESC, id DESC LIMIT #{sc.offset}, #{sc.pageSize}
