mapper.update:
UPDATE user_info
SET profile_img = #{profileImg}, nickname = #{nickname}, intro = #{intro}, instagram_id = #{instagramId},
    tel = #{tel}, pwd = #{pwd}
WHERE email = #{email}

UPDATE user_info
SET created_by = #{createdBy}
WHERE id = #{userId}

UPDATE user_info
SET updated_by = #{updatedBy}
WHERE id = #{userId}

UPDATE user_info
SET user_code = #{userCode}
WHERE id = #{userId}
